import { useState } from "react";
import { Button } from "@/components/ui/button";
import { X } from "lucide-react";

interface EventModalProps {
  title: string;
  description: string;
  children?: React.ReactNode;
}

const eventDetails = {
  "ROBO WAR": "Step into the thrilling arena of Robowar, where engineering ingenuity meets fierce competition. Witness custom-built machines clashing in an electrifying test of strategy, durability, and operator skill. With sparks flying and adrenaline surging, only the toughest bots will survive each round. Experience the excitement as teams push their robots‚Äîand nerves‚Äîto the limit in the quest for victory.",

  "ROBO SOCCER": "Join the fast-paced world of Robo Soccer, where small but mighty robots battle to score goals with precision and teamwork. The field comes alive with robotic players executing passes, dribbles, and unstoppable shots, all guided by talented human strategists. Each match is packed with robotic excitement, nail-biting moves, and a race against the clock to outscore the rivals.",

  "ROBO SUMO": "Enter the circle for Robo Sumo, a true test of balance, technique, and mechanical muscle. In this showdown, robots grapple and push in a test of strength, aiming to force their opponents out of the arena. Every second counts as competitors adjust tactics, maneuver for better positions, and boldly attempt to become the last bot standing in a battle of engineering wits.",

  "DRONE RACE": "Soar to new heights with the Drone Race, a mesmerizing spectacle of aerial skill and speed. Participants navigate challenging obstacle courses, weaving through tight gates and making split-second maneuvers while chasing the fastest lap time. The atmosphere buzzes with excitement as pilots demonstrate incredible control and daring, keeping the audience riveted to each suspenseful flight.",

  "OBSTACLE RACE": "Take on the ultimate Obstacle Race, where brave robots conquer a gauntlet of ramps, mud, and ever-changing terrain. Each turn presents new challenges‚Äîsteep climbs, tricky bridges, and surprise barriers. Will your robot maintain agility and traction? The event pushes designs to their limits, rewarding the teams with the most resilient and adaptable creations.",

  "LINE FOLLOWER": "Test the intelligence and finesse of your autonomous designs in the Line Follower event! Sleek, sensor-driven robots zoom around intricate tracks, relying only on their programming and sensors to stay on course. The competition is fierce as every millisecond counts, and only the sharpest bots with the best algorithms make it to the finish line in record time."
};

export default function EventModal({ title, description, children }: EventModalProps) {
  const [isOpen, setIsOpen] = useState(false);
  const eventDescription = eventDetails[title as keyof typeof eventDetails] || description;

  const handleOpen = () => {
    setIsOpen(true);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  return (
    <>
      <Button 
        onClick={handleOpen}
        variant="outline" 
        className="border-2 border-primary/50 text-primary hover:bg-primary/20 hover:border-primary transition-all duration-300 neon-border"
      >
        Details
      </Button>

      {isOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center">
          {/* Backdrop */}
          <div 
            className="fixed inset-0 bg-black/80 backdrop-blur-sm"
            onClick={handleClose}
          />

          {/* Modal Content */}
          <div className="relative z-50 w-full max-w-2xl mx-4 bg-card/95 backdrop-blur-md border-2 border-primary/30 rounded-xl p-6 neon-border">
            {/* Close Button */}
            <button 
              onClick={handleClose}
              className="absolute top-4 right-4 text-muted-foreground hover:text-foreground transition-colors"
            >
              <X className="w-6 h-6" />
            </button>

            {/* Content */}
            <div className="space-y-4">
              <h2 className="text-2xl font-bold text-primary mb-4">
                {title}
              </h2>
              <p className="text-muted-foreground leading-relaxed text-lg">
                {eventDescription}
              </p>
              {children}
            </div>
          </div>
        </div>
      )}
    </>
  );
}

